import re
import random

SERVER_REGEX = re.compile(r"^backend-\d+\.mydomain\.com$")

class LoadBalancer:
    def __init__(self):
        self.weights = {}

    def add_backend(self, backend, weight=1):
        if SERVER_REGEX.match(backend):
            self.weights[backend] = weight
        else:
            raise ValueError("Invalid backend")

    def get_backend(self):
        total = sum(self.weights.values())
        rnd = random.uniform(0, total)
        curr_sum = 0
        for backend, weight in self.weights.items():
            curr_sum += weight
            if rnd < curr_sum:
                return backend

load_balancer = LoadBalancer()
