import re

class UrlAnalyzer:

    def __init__(self):
        self.domain_counts = {}
        self.total_count = 0
        self.regex = re.compile(r"(?:www\.)?(?P<domain>[\w\.-]+\.[\w\.-]+)")

    def analyze(self, url):
        self.total_count += 1
        match = self.regex.match(url)
        if match:
            domain = match.group("domain")
            self.domain_counts[domain] = self.domain_counts.get(domain, 0) + 1

    def get_percentage(self, domain):
        count = self.domain_counts.get(domain, 0)
        return count / self.total_count * 100

    def top_domain(self):
        top_domain = max(self.domain_counts, key=self.domain_counts.get)
        return top_domain, self.get_percentage(top_domain)

analyzer = UrlAnalyzer()
analyzer.analyze("www.google.com")
analyzer.analyze("www.bing.com")
analyzer.analyze("www.yahoo.com")

top_domain, percentage = analyzer.top_domain()
print(f"Top domain: {top_domain} - {percentage:.1f}% of urls")

