import re

def build_url(base_url, path='', query_params={}):
    url = re.sub(r'/+$','', base_url)
    if path:
        url += '/' + path.lstrip('/')

    if query_params:
        query_string = urlencode(query_params)
        url += '?' + query_string

    parsed = urlparse(url)
    if parsed.scheme in {'http', 'https'}:
        return url
    else:
        return None

