class AffiliateRewriter:

    def __init__(self):
        self.affiliate_codes = {
            'www.google.com': 'abc123',
            'www.bing.com': 'def456',
            'search.yahoo.com': 'ghi789'
        }

        self.search_engine_pattern = re.compile(r'https?://(www\.google\.com|www\.bing\.com|search\.yahoo\.com)')

    def rewrite(self, url):
        if self.search_engine_pattern.match(url):
            for domain, code in self.affiliate_codes.items():
                if domain in url:
                    return url + '?affiliate=' + code
        return url

rewriter = AffiliateRewriter()
url = input('Enter a URL: ')
affiliate_url = rewriter.rewrite(url)
print(affiliate_url)

