import os

def index_file(path):
    with open(path, 'r') as f:
        text = f.read()

    index = {}
    for word in text.split():
        if word in index:
            index[word].append(path)
        else:
            index[word] = [path]

    with open('index.txt', 'w') as f:
        for word, locs in index.items():
            f.write(f'{word}:{",".join(locs)}\n')

